import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import { Button, Container, Form, FormGroup, Label, Input, Row, Col } from 'reactstrap';
import './App.css';

class SearchForm extends Component {
    constructor(props) {
      super(props);
    }

    render() {
        let { categories, address, areaChange, fullSearch, searchBusinesses, clearAllLists } = this.props;

        return (
					<Form className="yelpSearchForm">
						<Row form>
							<Col md={4}>
								<FormGroup>
									<Label for="categorySearch">Find</Label>
									<Input type="select" name="categorySearch" id="categorySearch" placeholder="Afghan, Hawaiian, Spanish...">
											{categories.map((category) => {
													// let title = Object.keys(category)[0];
													return (<option key={category} value={category}>{category}</option>);
											})}
									</Input>
								</FormGroup>
							</Col>
							<Col md={4}>
								<FormGroup>
									<Label for="addressSearch">Near</Label>
									<Input type="text" name="addressSearch" id="addressSearch" placeholder="1334 E St SE, Washington, D.C. 20003" 
											value={address} onChange={areaChange}></Input>
								</FormGroup>
							</Col>
							<Col md={4}>
								<FormGroup>
									<Button id="bizSearchBtn" onClick={searchBusinesses}>Find Businesses</Button>			
								</FormGroup>	
							</Col>
						</Row>
						<Button id="searchBtn" onClick={fullSearch}>Search (users & business)</Button>
						<Button id="bizSearchBtn" onClick={searchBusinesses}>Find Businesses</Button>
						<Button id="clearListsBtn" onClick={() => clearAllLists()}>Clear All</Button>
					</Form>
        );
    }

}

export default SearchForm;
